var ICON=new google.maps.MarkerImage("medicare.png",null,null,new google.maps.Point(14,13)),SHADOW=new google.maps.MarkerImage("medicare-shadow.png",null,null,new google.maps.Point(14,13));google.maps.event.addDomListener(window,"load",function(){var e=new google.maps.Map(document.getElementById("map-canvas"),{center:new google.maps.LatLng(-28,135),zoom:4,mapTypeId:google.maps.MapTypeId.ROADMAP}),t=document.getElementById("panel"),n=new MedicareDataSource,r=new storeLocator.View(e,n,{geolocation:!1,features:n.getFeatures()});r.createMarker=function(e){var t={position:e.getLocation(),icon:ICON,shadow:SHADOW,title:e.getDetails().title};return new google.maps.Marker(t)};var i=new InfoBubble;r.getInfoWindow=function(e){if(!e)return i;var t=e.getDetails(),n=['<div class="store"><div class="title">',t.title,'</div><div class="address">',t.address,"</div>",'<div class="hours misc">',t.hours,"</div></div>"].join("");i.setContent($(n)[0]);return i};new storeLocator.Panel(t,{view:r})});